var GameSettings = function(){
  var _this = this;
  this.buttons = globals.helper.findByUUID([
    user_config.unlimited_UUID.value,
    user_config.three_UUID.value,
    user_config.five_UUID.value,
    user_config.ten_UUID.value
  ]);
  Object.keys(this.buttons).forEach(function(button){
    _this.setupButton(button);
  });
  this.current_length = 1;
};
GameSettings.prototype = {
  setupButton:function(button){
    var _this = this;
    _this.buttons[button].addEventListener('cursorup',function(){
      _this.resetButtons();
      _this.buttons[button].material.color = new THREE.Color('#38d917');
      switch(button){
        case user_config.unlimited_UUID.value:
          _this.current_length = 1;
          break;
        case user_config.three_UUID.value:
          _this.current_length = 3;
          break;
        case user_config.five_UUID.value:
          _this.current_length = 5;
          break;
        case user_config.ten_UUID.value:
          _this.current_length = 10;
          break;
      }
    });
  },
  resetButtons:function(){
    var _this = this;
    Object.keys(this.buttons).forEach(function(button){
      _this.buttons[button].material.color = new THREE.Color('#cfcfcf');
    });
  }
};
object3d.userData._game_settings = new GameSettings();

