var World = function(){
	globals.helper.resetCannonWorld(object3d);
	this.world = globals.helper.getCannonWorld();
	this.world.broadphase = new CANNON.SAPBroadphase(this.world);
	this.world.gravity.set(0,-10,0);
	this.world.step(1/90);
	this.world.solver.iterations = 3;
	this.world.defaultContactMaterial.friction = 0;
	this.mass = 1;
	this.setupGround();
}
World.prototype = {
	setupGround:function(){
        object3d.userData.groundMaterial = new CANNON.Material("groundMaterial");
        var ground_ground_cm = new CANNON.ContactMaterial(object3d.userData.groundMaterial, object3d.userData.groundMaterial, {
            friction: 0.8,
            restitution: 0.3,
            contactEquationStiffness: 1e8,
            contactEquationRelaxation: 3,
            frictionEquationStiffness: 1e8,
            frictionEquationRegularizationTime: 3,
        });
        this.world.addContactMaterial(ground_ground_cm);
      	var groundShape = new CANNON.Plane();
		var groundBody = new CANNON.Body({ mass: 0, shape: groundShape, material: object3d.userData.groundMaterial });
		groundBody.quaternion.setFromAxisAngle(new CANNON.Vec3(1,0,0), -Math.PI/2);
		this.world.add(groundBody);
	},
	update:function(){
		globals.helper.updateCannonPhysics();
	}
};
object3d.userData._world = new World();

var Ball = function(){
	this.world = globals.helper.getCannonWorld();
	this.setupBall();
	if(user_config.ball_kicker_enabled.value){
		this.setupKicker();
	}
	this.setupGamePads();
};
Ball.prototype = {
	setupBall:function(){
		var _this = this;
		var ballShape = new CANNON.Sphere(user_config.ball_size.value);
		this.ballBody = new CANNON.Body({ mass: 1.0, shape: ballShape, material: object3d.userData.groundMaterial  });
		this.ballBody.position.set(-100,1,0);
		this.ballBody.angularDamping = 0.8;
		this.world.add(this.ballBody);
        globals.helper.addCannonMesh(this.ballBody,'#ffffff',object3d);
		this.ball = object3d.children[object3d.children.length-1];
		var ball = this.ball.children[this.ball.children.length-1];
		ball.material.map = new THREE.TextureLoader().load(user_config.ball_texture.value);
		ball.material.map.needsUpdate = true;
		this.ballBody.addEventListener("collide",function(e){
			if(e.body===object3d.userData._kicker){  
				if(object3d.userData._networking){
					object3d.userData._networking.takeOwnership();
					setTimeout(function(){
						object3d.userData._networking.update();
					},100);
				}
			}
		});
	},
	setupKicker:function(){
		var kicker_shape = new CANNON.Sphere(1);//new CANNON.Vec3(0.3, 0.3, 1)
		object3d.userData._kicker = new CANNON.Body({ mass: 0 });
		object3d.userData._kicker.addShape(kicker_shape,new CANNON.Vec3(0,-0.5,0));
		object3d.userData._kicker.position.set(0, 0.5, 0);
		this.world.add(object3d.userData._kicker);
		globals.helper.addCannonMesh(object3d.userData._kicker,'#ff00ff',object3d);
		this.kicker = object3d.children[object3d.children.length-1];
		this.kicker = this.kicker.children[this.kicker.children.length-1];

		this.kicker.material.opacity = 0.3;
		this.kicker.material.transparent = true;
		this.kicker.material.needsUpdate = true;
	},
	setupGamePads:function(){
		this.gamepadsList = altspace.getGamepads();
		var padL, padR;
		for(var i=0; i<this.gamepadsList.length; i++) {
			var curPadInfo = this.gamepadsList[i];
			switch(curPadInfo.mapping){
				case "standard":
					this.is_standard_controls = true;
					continue;
				case "touch":
				case "steamvr":
					this.mapping = curPadInfo.mapping;
					if (curPadInfo.hand == "left") {
						padL = curPadInfo;
					} else {
						padR = curPadInfo;
					}
					break;
				default:
					console.log("UNKNOWN CONTROLLER TYPE??",curPadInfo.mapping);
					break;
			}
		}
	},
	reset:function(position){
		this.ballBody.position.copy(position||new CANNON.Vec3(0,0.4,0));
		this.ballBody.velocity.set(0,0,0);
		this.ballBody.angularVelocity.set(0,0,0);
	},
	kick:function(override,is_eye,ball_position){
		var _this = this;
		if(object3d.userData._networking&&object3d.userData._networking.iOwn){
			var pos = globals.helper.scene_graph.context.spine.position;
			var point1 = new THREE.Vector3(pos.x,is_eye?pos.y-0.1:-1,pos.z);
			var point2 = _this.ball.position;
			var middle = globals.helper.scene_graph.context.getPointInBetweenByPerc(point1,point2,0.8);
			var dir = new THREE.Vector3().subVectors( point2, point1 ).normalize();			
			if(point1.distanceTo(point2)<4||override){
				var worldPoint = new CANNON.Vec3(middle.x,middle.y,middle.z);
				var force = new CANNON.Vec3(dir.x*(override?1500:1200),dir.y*(override?1500:1200),dir.z*(override?1500:1200));
				_this.ballBody.applyForce(force,worldPoint);
				if(override){
					object3d.userData._networking.updateKick(ball_position,worldPoint,force);
				}else{
					setTimeout(function(){
						object3d.userData._networking.update();
					},20);
				}
			}
		}
	},
	update:function(){
		if(object3d.userData._kicker&&globals.helper.scene_graph.context.spine){
		  if(object3d.userData._game_state.is_playing){
			  object3d.userData._kicker.position.x = globals.helper.scene_graph.context.spine.position.x;
		      object3d.userData._kicker.position.z = globals.helper.scene_graph.context.spine.position.z;
			  object3d.userData._kicker.position.y = 0.5;
		  }else{
			  object3d.userData._kicker.position.set(0, -1, 0);
		  }
		}
		this.getKicks();
	},
	getKicks:function(){

		//domPad.buttons[GRIPINDEX].pressed
	}
}
object3d.userData._ball = new Ball();

