var Networking = function(){
	var _this = this;
	this.socket = globals.helper.getSceneGraph().context.socket;
	this.socket.removeAllListeners('custom-sync');
	this.socket.on('custom-sync',this.sync.bind(this));
    this.current_owner = "";
	clearInterval(object3d.userData._networkingLoop);
	//object3d.userData._networkingLoop = setInterval(this.update.bind(this),500);
};
Networking.prototype = {
	sync:function(msg){
		var _this = this;
		switch(msg.type){
			case "game-state":
				if(object3d.userData._game_state)
					object3d.userData._game_state.setState(msg.state);
				break;
			case "take-ownership":
				this.iOwn = false;
				this.current_owner = msg.userId;
				this.someoneElseOwns = true;
				break;
			case "out-of-bounds":
				this.iOwn = false;
				this.someoneElseOwns = true;
				this.current_owner = msg.userId;
				object3d.userData._scoring_rules.setOutOfBoundsState(msg);
				break;
			case "kick-state":
				this.iOwn = false;
				this.someoneElseOwns = true;
				this.current_owner = msg.userId;
				object3d.userData._ball.reset(msg.ball_position);
				object3d.userData._scoring_rules.resume();
				object3d.userData._scoring_rules.resetTempBall();
				setTimeout(function(){
					object3d.userData._ball.ballBody.applyForce(new CANNON.Vec3(msg.force.x,msg.force.y,msg.force.z),new CANNON.Vec3(msg.position.x,msg.position.y,msg.position.z));
				},30);
				break;
			case "ball-state":			
				if(this.someoneElseOwns){
					if(object3d.userData._ball){
						object3d.userData._ball.ballBody.quaternion.copy(msg.state.q);
						object3d.userData._ball.ballBody.velocity.copy(msg.state.v);
						object3d.userData._ball.ballBody.position.copy(msg.state.p);
						object3d.userData._ball.ballBody.angularVelocity.copy(msg.state.a);
					}
				}
				break;
		}
	},
	takeOwnership:function(){
		if(!this.stopOwnership){
			this.iOwn = true;
			this.someoneElseOwns = false;
			this.current_owner = globals.helper.scene_graph.context.user.userId;
			this.socket.emit('custom-sync',{
				sid:globals.helper.getSceneGraph().context.space.sid,
				type:'take-ownership',
              	userId:this.current_owner
			});
		}
	},
	start:function(){
		this.current_player_id = globals.helper.getSceneGraph().context.user.userId;
	},
	getLocalBallState:function(){
		var pos = object3d.userData._ball.ballBody.position;
		var quat = object3d.userData._ball.ballBody.quaternion;
		var vel = object3d.userData._ball.ballBody.velocity;
		var ang = object3d.userData._ball.ballBody.angularVelocity;
		return {
			p:{x:pos.x,y:pos.y,z:pos.z},
			q:{x:quat.x,y:quat.y,z:quat.z,w:quat.w},
			v:{x:vel.x,y:vel.y,z:vel.z},
			a:{x:ang.x,y:ang.y,z:ang.z},
		};
	},
	update:function(_state){
		if(this.iOwn){
			var state = _state||this.getLocalBallState();
			this.socket.emit('custom-sync',{
				sid:globals.helper.getSceneGraph().context.space.sid,
				type:'ball-state',
				state:state
			});
		}
	},
	updateKick:function(ball_position,position,force){
		this.socket.emit('custom-sync',{
			sid:globals.helper.getSceneGraph().context.space.sid,
			type:'kick-state',
			ball_position:ball_position,
			position:position,
			force:force,
			userId:this.current_owner
		});
	},
	setOutOfBounds:function(position,is_kick,is_goal_kick){
		this.socket.emit('custom-sync',{
			sid:globals.helper.getSceneGraph().context.space.sid,
			type:'out-of-bounds',
			position:position,
			is_kick:is_kick,
			is_goal_kick:is_goal_kick,
			userId:this.current_owner
		});
	}
};
object3d.userData._networking = new Networking();

