var FlagFactory = function(){
  var modalUuid = user_config.model_UUID.value,
      containerUuid = user_config.container_UUID.value;
  var results = globals.helper.findByUUID([modalUuid,containerUuid]);
  if(results[modalUuid]){
    object3d.userData._clone = 
      JSON.stringify(globals.helper.scene_graph.serialiseScene(results[modalUuid]));
  }
  this.cloneContainer = results[containerUuid];
};
FlagFactory.prototype = {
  make:function(add_to_container){
    if(object3d.userData._clone){
      var dummy = new THREE.Object3D();
      globals.helper.scene_graph.deSerialiseScene(JSON.parse(object3d.userData._clone),dummy);
      if(add_to_container&&this.cloneContainer){
        _this.cloneContainer.add(dummy);  
      }
      return dummy;
    }
  },
  setColor:function(flag,color){
	  var is_set = false;
	  var _this = this;
		flag.traverse(function(child){
			if(child.name === "Flag_2"){
				child.material.color = new THREE.Color(color==='blue'?'#0000ff':color==='grey'?'#cfcfcf':'#ff0000');
				child.material.needsUpdate = true;
				is_set = true;
			}
		});
		if(!is_set){
			setTimeout(function(){
				_this.setColor(flag,color);
			},1000);
		}
  }
}
globals.helper.scene_graph.container.userData.flag_factory = new FlagFactory();

