    this.socket.removeAllListeners('get-state')
    this.socket.removeAllListeners('all-users');
    this.socket.on('all-users',this.setUserPlayers.bind(this));
    this.socket.on('get-state',this.setState.bind(this));
    this.socket.emit('get-state',{
