      type:'football'
    });
	this.socket.emit('all-users',{sid:globals.helper.scene_graph.context.space.sid});
  },
  getWhistleSound:function(is_ogg){
	  return new altspaceutil.behaviors.NativeComponent('n-sound',{src:'/images/whistle.'+(is_ogg?'ogg':'mp3'),autoplay: true,volume:10,on: 'begin',spatialBlend:1});
  },
  setUserPlayers:function(msg){
	  this.users = msg;
	  if(!this.is_playing&&!this.is_second_half)this.autoAllocatePlayers();
  },
  setState:function(msg){
    if(msg){
      this.is_second_half = msg.is_second_half;
      this.start_time = msg.start_time;
      this.second_start_time = msg.second_start_time;
      this.players_red = msg.players_red;
	  this.state = msg.state;
	  this.goals_blue = msg.goals_blue||0;
	  this.goals_red = msg.goals_red||0;
      this.players_blue = msg.players_blue;
	  this.current_length = msg.current_length;
	  this.score_board_settings_value.userData._game_settings.current_length = msg.current_length;
	  if(object3d.userData._team_flags)object3d.userData._team_flags.show(this.is_second_half);
	  this.setPlayerFlags();
	  if(msg.is_playing){
		  if(this.is_second_half){
			this.startSecondHalf(true);
		  }else{
			this.startGame(true);
		  }
	  }else if(msg.is_navigation){
			this.nextAction(msg.is_forward);
	  }
	  this.showUnusedPlayers();
    }
  },
  setRemoteState:function(is_navigation){
	this.current_length = this.score_board_settings_value.userData._game_settings.current_length;
	var state = {
		current_length: this.current_length,
		state:this.state,
		is_forward:this.is_forward,
		is_playing:this.is_playing,
		is_navigation:is_navigation,
		is_second_half:this.is_second_half,
		start_time:this.start_time,
		second_start_time:this.second_start_time,
		players_red:this.players_red,
		players_blue:this.players_blue,
		goals_blue:this.goals_blue||0,
		goals_red:this.goals_red||0,
	};
	this.socket.emit('set-state',{
      sid:globals.helper.scene_graph.context.space.sid,
      type:'football',
      value:state
    });
	this.socket.emit('custom-sync',{
      sid:globals.helper.scene_graph.context.space.sid,
      type:'game-state',
      state:state
    });
  },
  setupClose:function(){
		this.showUsedPlayers();
		this.setPlayerFlags();
		if(object3d.userData._team_flags)object3d.userData._team_flags.show();
	    this.score_board_title.getBehaviorByType('n-text').data.text = 'Game Complete\n\n'+this.goals_red+' - '+this.goals_blue;
		this.score_board_action.getBehaviorByType('n-text').data.text = 'Reset';
		this.score_board_reset.position.x = 0;
		this.score_board_reset.position.z = 0;
		this.score_board_back.position.z = -1;
		this.score_board_settings.position.z = -1;
		this.score_board_right.position.z = 0;
		this.score_board_flags.position.z = 0;
		this.score_board_timer.position.z = -1;
		this.score_board_left.position.z = 0;
		this.state = 'end-game';
  },
  setupHalfTime:function(){
	this.showUsedPlayers();
	this.setPlayerFlags();
	if(object3d.userData._team_flags)object3d.userData._team_flags.show(true);
	this.score_board_title.getBehaviorByType('n-text').data.text = 'Half Time\n\n'+this.goals_blue+' - '+this.goals_red;
    this.score_board_action.getBehaviorByType('n-text').data.text = 'Start';
    this.score_board_action.position.x = 0;
    this.score_board_action.position.z = 0;
    this.score_board_back.position.z = -1;
    this.score_board_settings.position.z = -1;
    this.score_board_right.position.z = 0;
    this.score_board_flags.position.z = 0;
    this.score_board_timer.position.z = -1;
    this.score_board_left.position.z = 0;
    this.state = 'half-time';
  },
  setupGame:function(){
    this.score_board_title.getBehaviorByType('n-text').data.text = 'Game Settings';
    this.score_board_action.getBehaviorByType('n-text').data.text = 'Next';
    this.score_board_action.position.x = 5;
    this.score_board_back.position.z = 0;
    this.score_board_back.position.x = -5;
    this.score_board_settings.position.z = 0;
	this.score_board_reset.position.z = -1;
    this.score_board_right.position.z = -1;
    this.score_board_flags.position.z = -1;
    this.score_board_left.position.z = -1;
    this.score_board_middle.position.z = -1;
	this.is_second_half = false;
	this.is_playing = false;
    this.state = 'game-settings';
	this.socket.emit('all-users',{sid:globals.helper.scene_graph.context.space.sid});
  },
  setupResetAction:function(){
    var _this = this;
	  this.score_board_reset.children[0].addEventListener('cursorup',function(){
		  if(_this.state === 'end-game'){
			  _this.has_been_reset = false;
			  _this.players_blue = [];
			  _this.players_red = [];
			  _this.goals_red = 0;
			  _this.goals_blue = 0;
			  _this.showUnusedPlayers();
			  _this.reset();
			  _this.is_forward = false;
			  _this.setRemoteState(true);
		  }else{
			  _this.players_blue = [];
			  _this.players_red = [];
			  _this.showUnusedPlayers();
			  _this.has_been_reset = true;
			  _this.score_board_player_flag_container.children
				.forEach(function(child){
					return globals.helper.scene_graph.container.userData.flag_factory.setColor(child,'grey');

				});
			  _this.setRemoteState();
		  }
	  });
  },
  setupBackAction:function(){
    var _this = this;
    this.score_board_back.children[0].addEventListener('cursorup',function(){
		_this.is_forward = false;
		_this.setRemoteState(true);
		_this.nextAction(false);
	});
  },
  setupToggleAction:function(){
	  var _this = this;
	  this.score_board_right_toggle.addEventListener('cursorup',function(event){
		  var unused = _this.unusedPlayers();
		  var point = -(event.point.y - 19.4);
		  var index = Math.floor(point/1.08);
		  if(unused[index])_this.players_blue.push(unused[index]);
		  _this.showUnusedPlayers();
		  _this.setPlayerFlags();
		  _this.setRemoteState();
	  });
	  this.score_board_left_toggle.addEventListener('cursorup',function(event){
		  var unused = _this.unusedPlayers();
		  var point = -(event.point.y - 19.4);
		  var index = Math.floor(point/1.08);
		  if(unused[index])_this.players_red.push(unused[index]);
		  _this.showUnusedPlayers();
		  _this.setPlayerFlags();
		  _this.setRemoteState();
	  });
  },
  nextAction:function(is_forward){
	  switch(this.state){
		case "ready":
		  return (is_forward?this.setupGame():this.reset());
		case "game-settings":
		  return (is_forward?this.setupPlayers():this.reset());
		case "player-settings":
		  return (is_forward?this.setupStartGame():this.setupGame());
		case "pre-start":
		  if(is_forward){
			  this.start_time = globals.helper.scene_graph.getServerTime();
			  this.startGame();
		  }else{
			  this.setupPlayers();
		  }
		  break;
		case "half-time":
		  this.second_start_time = globals.helper.scene_graph.getServerTime();
		  this.startSecondHalf();
		  break;
		default:
		  this.reset();
	  }
  },
  setupNextAction:function(){
    var _this = this;
    this.score_board_action.children[0].addEventListener('cursorup',function(){
		_this.is_forward = true;
		if(["half-time","pre-start"].indexOf(_this.state)===-1)_this.setRemoteState(true);
		_this.nextAction(true);
	});
  },
  update:function(serverTime){
	  this.serverTime = serverTime;
	  if(this.is_playing){
		  var now = serverTime;
		  var time = 0;
		  if(this.is_second_half){
			 time = now-this.second_start_time;
		  }else{
			 time = now-this.start_time;
		  }
		  var date_time = new Date(time);
		  this.score_board_timer.getBehaviorByType('n-text').data.text = ("0"+date_time.getMinutes()).slice(-2)+':'+("0"+date_time.getSeconds()).slice(-2);
		  var limit = this.current_length*60*1000;
		  if(time>=limit){
			  this.is_playing = false;
				object3d.userData._scoring_rules.temp_ball.position.set(0,-1,0);
				object3d.userData._scoring_rules.resume();
			  this.is_second_half = !this.is_second_half;
			  if(this.is_second_half){
				  this.setupHalfTime();
			  }else{
				  this.setupClose();
			  }
			  object3d.userData._ball.ball.removeAllBehaviors();
			  this.playWhistle();
			  object3d.userData._ball.reset(new THREE.Vector3(100,1,100));
		  }
	  }
  },
  isStart:function(){
	  var time = 0;
	  if(this.is_second_half){
		 time = this.serverTime-this.second_start_time;
	  }else{
		 time = this.serverTime-this.start_time;
	  }
	  return time<2;
  },
  playWhistle:function(){
	object3d.userData._ball.ball.removeAllBehaviors();
	object3d.userData._ball.ball.addBehaviors(this.getWhistleSound(true),this.getWhistleSound());
  },
  startGame:function(from_remote){
    this.is_playing = true;
	this.score_board_title.getBehaviorByType('n-text').data.text = '\n\n'+this.goals_red+' - '+this.goals_blue;
	//if(this.isStart()){
		this.playWhistle();
	//}
	object3d.userData._ball.reset();
	this.score_board_timer.position.z = 0;
    this.score_board_left.position.z = 0;
    this.score_board_right.position.z = 0;
    this.score_board_flags.position.z = 0;
    this.score_board_settings.position.z = -1;
    this.score_board_middle.position.z = -1;
	this.score_board_reset.position.z = -1;
    this.score_board_action.position.z = -1;
    this.score_board_back.position.z = -1;
    if(!from_remote)this.setRemoteState();
	this.showUnusedPlayers();
  },
  startSecondHalf:function(from_remote){
    this.is_playing = true;
	this.score_board_title.getBehaviorByType('n-text').data.text = '\n\n'+this.goals_blue+' - '+this.goals_red;
	object3d.userData._ball.reset();
	this.playWhistle();
	this.score_board_timer.position.z = 0;
    this.score_board_left.position.z = 0;
    this.score_board_right.position.z = 0;
    this.score_board_flags.position.z = 0;
    this.score_board_settings.position.z = -1;
    this.score_board_middle.position.z = -1;
	this.score_board_reset.position.z = -1;
    this.score_board_action.position.z = -1;
    this.score_board_back.position.z = -1;
    if(!from_remote)this.setRemoteState();
	this.showUnusedPlayers();
  },
  setPlayerFlag:function(color,user){
	  var _this = this;
		var already_exists_index = this.score_board_player_flag_container.children
			.map(function(child){
				return child.userData._player_object.userId;
			})
			.indexOf(user.userId);
			var flag;
		if(already_exists_index===-1){
			flag = globals.helper.scene_graph.container.userData.flag_factory.make();
			this.score_board_player_flag_container.add(flag);
			user.color = color;
			flag.userData._player_object = user;
			flag.userData._player_color = color;
			flag.position.y = 2.5;
			flag.addBehavior(globals.helper.scene_graph.behaviourFactory('n-skeleton-parent',{
				index: 0,
				part:'spine',
				side:'center',
				userId:user.userId,
			}));
		}else{
			flag = this.score_board_player_flag_container.children[already_exists_index];
			user.color = color;
			flag.userData._player_color = color;
			flag.position.y = 2.5;
		}
		globals.helper.scene_graph.container.userData.flag_factory.setColor(flag,color);
  },
  hidePlayerFlags:function(){
	  this.score_board_player_flag_container.children
		.forEach(function(child){
			child.position.y = -2.5;
		})
  },
  setPlayerFlags:function(){
	this.players_blue.forEach(this.setPlayerFlag.bind(this,'blue'));
	this.players_red.forEach(this.setPlayerFlag.bind(this,'red'));
  },
  setupStartGame:function(){
	this.setPlayerFlags();
    this.score_board_title.getBehaviorByType('n-text').data.text = '\n\nStart the game now!';
    this.score_board_action.getBehaviorByType('n-text').data.text = 'Start';
    this.score_board_settings.position.z = -1;
    this.score_board_back.position.z = 0;
    this.score_board_back.position.x = -5;
    this.score_board_left.position.z = -1;
    this.score_board_right.position.z = -1;
    this.score_board_flags.position.z = -1;
    this.score_board_middle.position.z = -1;
	this.score_board_reset.position.z = -1;
    this.score_board_action.position.x = 5;
    this.score_board_back.position.x = -5;
    this.state = 'pre-start';
  },
  unusedPlayers:function(){
	  var _this = this;
	  var users = this.users.filter(function(user){
		  var mapFn = function(d){return d.userId;};
		  return _this.players_blue.map(mapFn)
			.concat(_this.players_red.map(mapFn))
			.indexOf(user.userId)===-1;
	  });
	  return users;
  },
  showUsedPlayers:function(){
	  var _this = this;	  
	  var mapFn = function(d,color){
		  var name;
		  d.goals = d.goals||0;
		  if(d.userId === globals.helper.scene_graph.context.user.userId){
			  _this.my_user = d;
		  }
		  if(_this.is_playing||_this.is_second_half||_this.state==="end-game"||_this.state === "half-time"){
			  if(color==='blue'){
				  if(_this.is_second_half){
					  name = d.goals+" - "+d.displayName;
				  }else{
					  name = d.displayName+" - "+d.goals;
				  }
			  }else{
				  if(_this.is_second_half){
					  name = d.displayName+" - "+d.goals;
				  }else{
					  name = d.goals+" - "+d.displayName;
				  }
			  }
		  }else{
			  name = d.displayName;
		  }

			return name;
		};
	(this.is_second_half?this.score_board_right:this.score_board_left).getBehaviorByType('n-text').data.text = this.players_red.map(function(d){return mapFn(d,'red')}).join('\n');
  	(this.is_second_half?this.score_board_left:this.score_board_right).getBehaviorByType('n-text').data.text = this.players_blue.map(function(d){return mapFn(d,'blue')}).join('\n');
  },
  showUnusedPlayers:function(){
	  var _this = this;	  
	  var unused_players = this.unusedPlayers();
	  this.score_board_middle.getBehaviorByType('n-text').data.text = unused_players.map(function(d){return d.displayName;}).join('\n');
	  this.showUsedPlayers();
	  this.score_board_left_toggle.getBehaviorByType('n-text').data.text = unused_players.map(function(d){return '<color=#0f8f39><';}).join('\n');
	  this.score_board_right_toggle.getBehaviorByType('n-text').data.text = unused_players.map(function(d){return '<color=#0f8f39>>';}).join('\n');
      if(this.is_playing||this.is_second_half||this.state==="end-game"||this.state === "half-time"){
		  this.score_board_title.getBehaviorByType('n-text').data.text = this.is_second_half?'\n\n'+this.goals_blue+' - '+this.goals_red:'\n\n'+this.goals_red+' - '+this.goals_blue;
	  }
 },
  autoAllocatePlayers:function(){
	 if(!this.has_been_reset&&this.users.length){
		var middle = Math.floor(this.users.length/2);
		this.players_red = this.users.slice(0,middle);
		this.players_blue = this.users.slice(middle,this.users.length);
	}
  },
  setupPlayers:function(){
    if(object3d.userData._team_flags)object3d.userData._team_flags.show();
    this.score_board_title.getBehaviorByType('n-text').data.text = 'Team Settings';
	this.autoAllocatePlayers();
	this.setPlayerFlags();
	this.showUnusedPlayers();
	this.score_board_action.getBehaviorByType('n-text').data.text = 'Next';
	this.score_board_action.position.x = 10;
    this.score_board_back.position.x = -10;
    this.score_board_back.position.z = 0;
    this.score_board_settings.position.z = -1;
	this.score_board_reset.position.z = 0;

    this.score_board_left.position.z = 0;
    this.score_board_right.position.z = 0;
    this.score_board_middle.position.z = 0;
    this.score_board_flags.position.z = 0;
    this.state = 'player-settings';
  },
  setupElements:function(){
    this.elements = globals.helper.findByUUID([
      user_config.title_UUID.value,
      user_config.left_UUID.value,
      user_config.right_UUID.value,
      user_config.middle_UUID.value,
      user_config.action_UUID.value,
	  user_config.reset_UUID.value,
      user_config.flags_UUID.value,
      user_config.settings_UUID.value,
      user_config.back_UUID.value,
      user_config.settings_value_UUID.value,
      user_config.right_toggle_UUID.value,
      user_config.left_toggle_UUID.value,
	  user_config.timer_UUID.value,
	  user_config.player_flag_container_UUID.value
    ]);
    this.score_board_title = this.elements[user_config.title_UUID.value];
    this.score_board_left = this.elements[user_config.left_UUID.value];
    this.score_board_right = this.elements[user_config.right_UUID.value];
    this.score_board_middle = this.elements[user_config.middle_UUID.value];
    this.score_board_action = this.elements[user_config.action_UUID.value];
    this.score_board_reset = this.elements[user_config.reset_UUID.value];
    this.score_board_flags = this.elements[user_config.flags_UUID.value];
    this.score_board_settings = this.elements[user_config.settings_UUID.value];
    this.score_board_back = this.elements[user_config.back_UUID.value];
    this.score_board_settings_value = this.elements[user_config.settings_value_UUID.value];
    this.score_board_right_toggle = this.elements[user_config.right_toggle_UUID.value];
    this.score_board_left_toggle = this.elements[user_config.left_toggle_UUID.value];
    this.score_board_timer = this.elements[user_config.timer_UUID.value];
    this.score_board_player_flag_container = this.elements[user_config.player_flag_container_UUID.value];


  },
  reset:function(){
      if(object3d.userData._team_flags)object3d.userData._team_flags.show();
	  if(object3d.userData._scoring_rules){
		  object3d.userData._scoring_rules.temp_ball.position.set(0,-1,0);
		  object3d.userData._scoring_rules.resume();
	  }
	  if(object3d.userData._ball){
		  object3d.userData._ball.reset(new THREE.Vector3(100,1,100));
	  }
	  this.hidePlayerFlags();
	  this.start_time = 0;
	  this.second_start_time = 0;
	  this.is_second_half = false;
	  this.is_playing = false;
	  this.players_blue = [];
	  this.players_red = [];
	  this.goals_blue = 0;
	  this.goals_red = 0;
	  this.is_forward = false;
      this.score_board_title.getBehaviorByType('n-text').data.text = '\nClick Start to begin';
      this.score_board_action.getBehaviorByType('n-text').data.text = 'Start';
      this.score_board_settings.position.z = -1;
      this.score_board_left.position.z = -1;
      this.score_board_right.position.z = -1;
      this.score_board_flags.position.z = -1;
      this.score_board_action.position.x = 0;
      this.score_board_action.position.z = 0;
	  this.score_board_timer.position.z = -1;
      this.score_board_middle.position.z = -1;
	  this.score_board_reset.position.z = -1;
      this.score_board_back.position.z = -1;
	  this.score_board_back.position.x = -5;
      this.state = 'ready';
  }
};
