TeamFlags.prototype = {
	show:function(is_second_half){
		var _this = this;
      globals.helper.scene_graph.container.userData.flag_factory.setColor(object3d.userData._left_flag,is_second_half?'blue':'red');
	  globals.helper.scene_graph.container.userData.flag_factory.setColor(object3d.userData._right_flag,is_second_half?'red':'blue');
	  this.left_goal.children.forEach(function(child){
		  _this.updateNetItem(child,'left',is_second_half)
	  });
	  this.right_goal.children.forEach(function(child){
		  _this.updateNetItem(child,'right',is_second_half)
	  });
	},
	updateNetItem:function(child,side,is_second_half){
		var _this = this;
	  if(child.name==="Primitive"&&!child.children.length&&child.geometry){
		if((child.rotation.x===Math.PI/2&&child.rotation.z===Math.PI/2)||
			child.rotation.y===-Math.PI/2){
			_this.setNetTexture(true,child,is_second_half,side);
		}else if(child.rotation.z===Math.PI/2){
			_this.setNetTexture(false,child,is_second_half,side);
		}
	  }
	},
	setNetTexture:function(is_long,child,is_second_half,side){
		var texture = new THREE.TextureLoader().load(
			(side==='left'&&is_second_half)||(side==='right'&&!is_second_half)?
			this.red_goal_image:
			this.blue_goal_image
		);
		texture.wrapS = texture.wrapT = THREE.RepeatWrapping;
		texture.repeat.set(is_long?3:1,1)
		child.material.map = texture;
		texture.needsUpdate = true;
	}
};
