var ScoringRules = function(){
	this.setup(user_config.rules_type.value);
	this.out_of_bounds = false;
	this.is_throw_in = false;
	this.is_goal_kick = false;
	this.is_corner_kick = false;
	this.corner_kick_side = 'left';
	this.setupTempBall();
	this.playing_box = {
		top:user_config.playspace_width.value/2,
		left:user_config.playspace_length.value/2,
		right:-user_config.playspace_length.value/2,
		bottom:-user_config.playspace_width.value/2
	}
};
ScoringRules.prototype = {
	setup:function(type){
		console.log(type);
	},
	setupTempBall:function(){
		var _this = this;
		var sphere_geometry = new THREE.SphereGeometry(0.300, 16, 16);
		var outer_sphere_geometry = new THREE.SphereGeometry(0.350, 16, 16);
		this.temp_ball = new THREE.Group();
		this.temp_ball.add(new THREE.Mesh(sphere_geometry, new THREE.MeshBasicMaterial({map:new THREE.TextureLoader().load('https://i.imgur.com/TBd1wt6.jpg')})));
		this.temp_ball_material = new THREE.MeshBasicMaterial({transparent:true,opacity:0.1,color:'#fff'});
		this.temp_ball.add(new THREE.Mesh(outer_sphere_geometry, this.temp_ball_material));
		this.temp_ball.position.set(0,-1,0);
		this.message = new THREE.Mesh(new THREE.PlaneGeometry(1,1),new THREE.MeshBasicMaterial({visible:false}));
		this.message.scale.set(0.6,0.6,0.6);
		this.message.addBehaviors(
			new altspaceutil.behaviors.NativeComponent('n-text',{text:'click me',fontSize:2,width:2}),
			new altspaceutil.behaviors.NativeComponent('n-billboard'),
			new altspaceutil.behaviors.NativeComponent('n-mesh-collider',{type: 'hologram'})
		);
		this.temp_ball.add(this.message);
		globals.helper.scene_graph.context.simulation.scene.add(this.temp_ball);
		this.message.addEventListener('cursorup',function(){
			if((
			(_this.current_owner.color!==object3d.userData._game_state.my_user.color&&!_this.is_own_goal)||
			(_this.current_owner.color===object3d.userData._game_state.my_user.color&&_this.is_own_goal)
			)&&_this.out_of_bounds){
				var player_pos = globals.helper.scene_graph.context.spine.position;
				if(!(player_pos.z<_this.playing_box.top&&
				player_pos.z>_this.playing_box.bottom&&
				player_pos.x<_this.playing_box.left&&
				player_pos.x>_this.playing_box.right)||_this.is_goal_kick){
					_this.temp_ball.position.set(0,-1,0);
					object3d.userData._ball.reset(_this.throw_in_position);
					object3d.userData._networking.stopOwnership = false;
					object3d.userData._networking.takeOwnership();
					_this.resume();
					setTimeout(function(){
						object3d.userData._ball.kick(true,!_this.is_kick,_this.throw_in_position);
					},30);
				}
			}
		});
	},
	update:function(){
		var _this = this;
		if(object3d.userData._game_state&&object3d.userData._game_state.is_playing&&!this.out_of_bounds&&object3d.userData._networking.iOwn){
			var ball_position = object3d.userData._ball.ballBody.position.clone();
			if(!(ball_position.z<this.playing_box.top&&
				ball_position.z>this.playing_box.bottom&&
				ball_position.x<this.playing_box.left&&
				ball_position.x>this.playing_box.right)){
				object3d.userData._ball.reset(new THREE.Vector3(100,1,100));
				this.out_of_bounds = true;
				if(ball_position.z>=this.playing_box.top||ball_position.z<=this.playing_box.bottom){
					this.throwIn(ball_position);
				}else if(ball_position.x>=this.playing_box.left||ball_position.x<=this.playing_box.right){
					if((ball_position.z<=user_config.scoring_width.value/2&&
						ball_position.z>=-user_config.scoring_width.value/2)&&
						 ball_position.y<=user_config.scoring_height.value){
							this.goal(ball_position.x>0?'left':'right',ball_position);
					}else{
						this.cornerKick(ball_position);
					}
				}
			}
		}
	},
	goal:function(side,position){
		if(object3d.userData._game_state)object3d.userData._game_state.playWhistle();
		this.current_owner = this.getCurrentOwner(true);
		if(object3d.userData._game_state){
			if(object3d.userData._game_state.is_second_half){
				if(side==='left'){
					object3d.userData._game_state.goals_blue++;
				}else{
					object3d.userData._game_state.goals_red++;
				}
			}else{
				if(side==='left'){
					object3d.userData._game_state.goals_red++;
				}else{
					object3d.userData._game_state.goals_blue++;
				}
			}
			if(side==='right'){
				this.temp_ball.position.copy(new THREE.Vector3((-user_config.playspace_length.value/2)+13.75,0.4,0));
			}else{
				this.temp_ball.position.copy(new THREE.Vector3((user_config.playspace_length.value/2)-13.75,0.4,0));
			}
			if((this.current_owner.color==='blue'&&side==='right'&&object3d.userData._game_state.is_second_half)||
				(this.current_owner.color==='blue'&&side==='left'&&!object3d.userData._game_state.is_second_half)||
				(this.current_owner.color==='red'&&side==='right'&&!object3d.userData._game_state.is_second_half)||
				(this.current_owner.color==='red'&&side==='left'&&object3d.userData._game_state.is_second_half)){
					this.is_own_goal = true;
				}else{
					this.is_own_goal = false;
				}
			this.is_goal_kick = true;
			this.throw_in_position = this.temp_ball.position.clone();
			console.log('is_own_goal',this.is_own_goal,this.current_owner.color,side,object3d.userData._game_state.is_second_half);
			this.setTempBall(false); 
			this.message.position.set(0,0.5,0);
			this.is_kick = true;

			object3d.userData._game_state.showUnusedPlayers();
			if(object3d.userData._networking.iOwn&&object3d.userData._game_state)
				object3d.userData._game_state.setRemoteState();
			object3d.userData._networking.setOutOfBounds(this.throw_in_position,true,true);


		}

	},
	setTempBall:function(is_throw){
		var _this = this;
		if(this.current_owner.color==='blue'&&!this.is_own_goal){
			this.temp_ball_material.color = new THREE.Color('#ff0000');
			this.message.getBehaviorByType('n-text').data.text = "<color=red>red to "+(is_throw?"throw":"kick")+" in\n<color=white>click here";
		}else if(this.current_owner.color==='red'&&!this.is_own_goal){
			this.temp_ball_material.color = new THREE.Color('#0000ff');
			this.message.getBehaviorByType('n-text').data.text = "<color=blue>blue to "+(is_throw?"throw":"kick")+" in\n<color=white>click here";
		}else if(this.current_owner.color==='red'&&this.is_own_goal){
			this.temp_ball_material.color = new THREE.Color('#ff0000');
			this.message.getBehaviorByType('n-text').data.text = "<color=red>red to "+(is_throw?"throw":"kick")+" in\n<color=white>click here";
		}else if(this.current_owner.color==='blue'&&this.is_own_goal){
			this.temp_ball_material.color = new THREE.Color('#0000ff');
			this.message.getBehaviorByType('n-text').data.text = "<color=blue>blue to "+(is_throw?"throw":"kick")+" in\n<color=white>click here";
		}
		//if(_this.current_owner.color===object3d.userData._game_state.my_user.color&&!this.is_own_goal){
			//this.resetTempBall();
		//}else if(_this.current_owner.color!==object3d.userData._game_state.my_user.color&&this.is_own_goal){
			//this.resetTempBall();
		//}
	},
	setOutOfBoundsState:function(msg){
		console.log('got oob',msg);
		if(this.reset_tween)this.reset_tween.stop();
		object3d.userData._game_state.playWhistle();
		this.out_of_bounds = true;
		this.current_owner = this.getCurrentOwner();
		this.throw_in_position = msg.position;
		this.temp_ball.position.copy(msg.position);
		this.setTempBall(!msg.is_kick); 
		this.message.position.set(0,msg.is_kick?0.5:-0.5,0);
		this.is_kick = msg.is_kick;
		this.is_goal_kick = msg.is_goal_kick;
		object3d.userData._ball.reset(new THREE.Vector3(100,1,100));
	},
	throwIn:function(position){
		if(object3d.userData._game_state)object3d.userData._game_state.playWhistle();
		this.current_owner = this.getCurrentOwner();
		this.throw_in_position = new THREE.Vector3(position.x,2,position.z>0?(user_config.playspace_width.value/2)-0.01:(-user_config.playspace_width.value/2)+0.01);
		this.temp_ball.position.copy(this.throw_in_position);
		this.setTempBall(true);
		this.message.position.set(0,-0.5,0);
		this.is_kick = false;
		object3d.userData._networking.setOutOfBounds(this.throw_in_position,false,false);
		//console.log(this.current_owner.color,this.throw_in_position);
	},
	resetTempBall:function(){
		this.reset_tween = new TWEEN.Tween(this.temp_ball.position)
			.to({ x: this.temp_ball.position.x, y:-1, z:this.temp_ball.position.z }, 8000)
			.easing(TWEEN.Easing.Elastic.Out)
			.start();
	},
	getCornerPosition:function(position){
		switch(true){

			case this.current_owner.color==="blue"&&position.x>0&&!object3d.userData._game_state.is_second_half:
				return new THREE.Vector3(((user_config.playspace_length.value-0.002)/2),0.4,(position.z>0?user_config.playspace_width.value-0.002:-user_config.playspace_width.value+0.002)/2);
			case this.current_owner.color==="blue"&&position.x<0&&object3d.userData._game_state.is_second_half:
				return new THREE.Vector3(((-user_config.playspace_length.value+0.002)/2),0.4,(position.z>0?user_config.playspace_width.value-0.002:-user_config.playspace_width.value+0.002)/2);


			case this.current_owner.color==="red"&&position.x>0&&object3d.userData._game_state.is_second_half:
				return new THREE.Vector3(((user_config.playspace_length.value-0.002)/2),0.4,(position.z>0?user_config.playspace_width.value-0.002:-user_config.playspace_width.value+0.002)/2);
			case this.current_owner.color==="red"&&position.x<0&&!object3d.userData._game_state.is_second_half:
				return new THREE.Vector3(((-user_config.playspace_length.value+0.002)/2),0.4,(position.z>0?user_config.playspace_width.value-0.002:-user_config.playspace_width.value+0.002)/2);




			case this.current_owner.color==="red"&&position.x<0&&!object3d.userData._game_state.is_second_half:
				this.is_goal_kick = true;
				return new THREE.Vector3(((-user_config.playspace_length.value+0.002)/2)+13.75,0.4,0)

			case this.current_owner.color==="red"&&position.x<0&&object3d.userData._game_state.is_second_half:
				this.is_goal_kick = true;
				return new THREE.Vector3(((-user_config.playspace_length.value+0.002)/2)+13.75,0.4,0)



			case this.current_owner.color==="red"&&position.x>0&&!object3d.userData._game_state.is_second_half:
				this.is_goal_kick = true;
				return new THREE.Vector3(((user_config.playspace_length.value-0.002)/2)-13.75,0.4,0)

			case this.current_owner.color==="red"&&position.x>0&&object3d.userData._game_state.is_second_half:
				this.is_goal_kick = true;
				return new THREE.Vector3(((user_config.playspace_length.value-0.002)/2)-13.75,0.4,0)



			case this.current_owner.color==="blue"&&position.x<0&&object3d.userData._game_state.is_second_half:
				this.is_goal_kick = true;
				return new THREE.Vector3(((-user_config.playspace_length.value+0.002)/2)+13.75,0.4,0)

			case this.current_owner.color==="blue"&&position.x<0&&!object3d.userData._game_state.is_second_half:
				this.is_goal_kick = true;
				return new THREE.Vector3(((-user_config.playspace_length.value+0.002)/2)+13.75,0.4,0)



			case this.current_owner.color==="blue"&&position.x>0&&object3d.userData._game_state.is_second_half:
				this.is_goal_kick = true;
				return new THREE.Vector3(((user_config.playspace_length.value-0.002)/2)-13.75,0.4,0)

			case this.current_owner.color==="blue"&&position.x>0&&!object3d.userData._game_state.is_second_half:
				this.is_goal_kick = true;
				return new THREE.Vector3(((user_config.playspace_length.value-0.002)/2)-13.75,0.4,0)


		}
	},
	cornerKick:function(position){
		object3d.userData._game_state.playWhistle();
		this.current_owner = this.getCurrentOwner();
		this.throw_in_position = this.getCornerPosition(position);
		this.temp_ball.position.copy(this.throw_in_position);
		this.setTempBall(false); 
		this.message.position.set(0,0.5,0);
		this.is_kick = true;
		object3d.userData._networking.setOutOfBounds(this.throw_in_position,true,this.is_goal_kick);
	},
	getCurrentOwner:function(should_increment_goals){
		if(object3d.userData._networking&&object3d.userData._networking.current_owner&&object3d.userData._game_state){
			var mapFn = function(u){return u.userId};
			var red_index = object3d.userData._game_state.players_red.map(mapFn)
			.indexOf(object3d.userData._networking.current_owner);
			var blue_index = object3d.userData._game_state.players_blue.map(mapFn)
			.indexOf(object3d.userData._networking.current_owner);

			if(should_increment_goals){
				if(red_index>-1){
					object3d.userData._game_state.players_red[red_index].goals= object3d.userData._game_state.players_red[red_index].goals||0;
					object3d.userData._game_state.players_red[red_index].goals++;
				}else if(blue_index>-1){
					object3d.userData._game_state.players_blue[blue_index].goals= object3d.userData._game_state.players_blue[blue_index].goals||0;
					object3d.userData._game_state.players_blue[blue_index].goals++;
				}
			}
		  return red_index>-1?object3d.userData._game_state.players_red[red_index]:object3d.userData._game_state.players_blue[blue_index];
		}
	},
	resume:function(){
		this.out_of_bounds = false;
		this.is_throw_in = false;
		this.is_corner_kick = false;
		this.is_goal_kick = false;
		this.is_own_goal = false;
		this.is_kick = false;
	}
};
